import{_ as a,o as s,c as e,Q as l}from"./chunks/framework.7b60570d.js";const m=JSON.parse('{"title":"Linux 基础知识","description":"","frontmatter":{},"headers":[],"relativePath":"workflow/devops/linux.md","lastUpdated":1704206502000}'),n={name:"workflow/devops/linux.md"},t=l(`<h1 id="linux-基础知识" tabindex="-1">Linux 基础知识 <a class="header-anchor" href="#linux-基础知识" aria-label="Permalink to &quot;Linux 基础知识&quot;">​</a></h1><blockquote><p>汇总了在工作中经常使用的 <code>Linux</code> 命令</p></blockquote><h2 id="linux-常见命令" tabindex="-1">Linux 常见命令 <a class="header-anchor" href="#linux-常见命令" aria-label="Permalink to &quot;Linux 常见命令&quot;">​</a></h2><h3 id="一、linux-常见目录" tabindex="-1">一、Linux 常见目录 <a class="header-anchor" href="#一、linux-常见目录" aria-label="Permalink to &quot;一、Linux 常见目录&quot;">​</a></h3><table><thead><tr><th>目录</th><th>解释</th></tr></thead><tbody><tr><td><code>/</code></td><td>根目录</td></tr><tr><td><code>～</code></td><td>当前用户家目录</td></tr><tr><td><code>/boot</code></td><td>启动目录，启动相关文件</td></tr><tr><td><code>/dev</code></td><td>设备文件</td></tr><tr><td><code>/etc</code></td><td>配置文件</td></tr><tr><td><code>/lib</code></td><td>系统库保存目录</td></tr><tr><td><code>/mnt</code></td><td>移动设备挂载目录</td></tr><tr><td><code>/media</code></td><td>光盘挂载目录</td></tr><tr><td><code>/misc</code></td><td>磁带机挂载目录</td></tr><tr><td><code>/root</code></td><td>超级用户的家目录,可以操作</td></tr><tr><td><code>/home</code></td><td>普通用户的家目录,可以操作</td></tr><tr><td><code>/tmp</code></td><td>临时目录,可以操作</td></tr><tr><td><code>/var</code></td><td>变量</td></tr><tr><td><code>/bin</code></td><td>普通的基本命令，如<code>ls</code>,<code>chmod</code>等, 普通用户都可使用</td></tr><tr><td><code>/sbin</code></td><td>基本的系统命令，如 <code>shutdown</code>，<code>reboot</code>，用于启动系统，修复系统,仅管理员才可运行</td></tr></tbody></table><h3 id="二、文件处理命令" tabindex="-1">二、文件处理命令 <a class="header-anchor" href="#二、文件处理命令" aria-label="Permalink to &quot;二、文件处理命令&quot;">​</a></h3><h4 id="_2-1、mkdir" tabindex="-1">2.1、<strong>mkdir</strong> <a class="header-anchor" href="#_2-1、mkdir" aria-label="Permalink to &quot;2.1、**mkdir**&quot;">​</a></h4><ul><li>创建目录，<code>mkdir directory</code></li><li><code>-p</code>, 递归创建目录</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 递归创建 b,c 目录</span></span>
<span class="line"><span style="color:#FFCB6B;">mkdir</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-p</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">b/c</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h4 id="_2-2、cd" tabindex="-1">2.2、<strong>cd</strong> <a class="header-anchor" href="#_2-2、cd" aria-label="Permalink to &quot;2.2、**cd**&quot;">​</a></h4><ul><li>切换所在目录，<code>change directory</code></li></ul><table><thead><tr><th>目录</th><th>解释</th></tr></thead><tbody><tr><td><code>~</code></td><td>家目录</td></tr><tr><td><code>.</code></td><td>当前目录</td></tr><tr><td><code>..</code></td><td>上级目录</td></tr></tbody></table><h4 id="_2-3、pwd" tabindex="-1">2.3、<strong>pwd</strong> <a class="header-anchor" href="#_2-3、pwd" aria-label="Permalink to &quot;2.3、**pwd**&quot;">​</a></h4><ul><li>显示当前目录</li></ul><h4 id="_2-4、rmdir" tabindex="-1">2.4、<strong>rmdir</strong> <a class="header-anchor" href="#_2-4、rmdir" aria-label="Permalink to &quot;2.4、**rmdir**&quot;">​</a></h4><ul><li>删除空目录, <code>remove empty directory</code></li><li><code>rmdir [目录名]</code></li></ul><h4 id="_2-5、rm" tabindex="-1">2.5、<strong>rm</strong> <a class="header-anchor" href="#_2-5、rm" aria-label="Permalink to &quot;2.5、**rm**&quot;">​</a></h4><ul><li><p>删除文件或者目录, <code>remove</code></p></li><li><p><code>rm [文件或者目录]</code></p><ul><li><code>-r</code> 删除目录</li><li><code>-f</code> 强制删除</li></ul></li><li><p><code>rm -rf [文件或者目录]</code>, 递归强制删除所有目录</p></li></ul><h4 id="_2-6、cp" tabindex="-1">2.6、<strong>cp</strong> <a class="header-anchor" href="#_2-6、cp" aria-label="Permalink to &quot;2.6、**cp**&quot;">​</a></h4><ul><li><p>复制命令, <code>copy</code></p></li><li><p><code>copy [源文件或者目录] [目标文件]</code></p><ul><li><code>-r</code> 复制目录,默认是复制文件</li><li><code>-i</code> 会在复制文件的时候给提示,如果复制的目标文件存在,会给你提示是否要覆盖</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">mkdir</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">afolder</span></span>
<span class="line"><span style="color:#FFCB6B;">mkdir</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">bfolder</span></span>
<span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">afolder/</span></span>
<span class="line"><span style="color:#FFCB6B;">touch</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span></span>
<span class="line"><span style="color:#FFCB6B;">cp</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">~/bfolder/</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div></li></ul><h4 id="_2-7、mv" tabindex="-1">2.7、<strong>mv</strong> <a class="header-anchor" href="#_2-7、mv" aria-label="Permalink to &quot;2.7、**mv**&quot;">​</a></h4><ul><li>移动文件或者改名, <code>move</code></li><li><code>mv [源文件或者目录] [目标文件]</code></li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 重命名</span></span>
<span class="line"><span style="color:#FFCB6B;">mv</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 移动</span></span>
<span class="line"><span style="color:#FFCB6B;">mv</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">a/</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h4 id="_2-8、ln" tabindex="-1">2.8、<strong>ln</strong> <a class="header-anchor" href="#_2-8、ln" aria-label="Permalink to &quot;2.8、**ln**&quot;">​</a></h4><ul><li>链接命令, 生成链接文件, <code>link</code></li><li><code>ln -s [源文件] [目标文件]</code><ul><li><code>-s</code> 创建软链接</li></ul></li><li>修改任意一个文件，另一个都会改变</li><li>删除源文件，软链接不能使用</li><li>软链接源文件必须写绝对路径</li></ul><h3 id="三、文件搜索命令" tabindex="-1">三、文件搜索命令 <a class="header-anchor" href="#三、文件搜索命令" aria-label="Permalink to &quot;三、文件搜索命令&quot;">​</a></h3><h4 id="_3-1、locate" tabindex="-1">3.1、locate <a class="header-anchor" href="#_3-1、locate" aria-label="Permalink to &quot;3.1、locate&quot;">​</a></h4><p>安装</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">yum  -y install mlocate</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>特点</p><ul><li>在后台数据库中按文件名搜索，速度比较快</li><li>数据保存在<code>/var/lib/mlocate/mlocate.db</code> 后台数据库，默认每天更新一次</li><li>可以<code>updatedb</code>命令立刻更新数据库</li><li>只能搜索文件名</li></ul><h4 id="_3-2、whereis" tabindex="-1">3.2、whereis <a class="header-anchor" href="#_3-2、whereis" aria-label="Permalink to &quot;3.2、whereis&quot;">​</a></h4><p>搜索命令所在路径以及帮助文档所在位置</p><p><code>whereis 命令名</code></p><ul><li><code>-b</code> 只查找可执行文件</li><li><code>-m</code> 只查找帮助文件</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># whereis cd</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># cd: /usr/bin/cd /usr/share/man/man1/cd.1.gz</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h4 id="_3-3、which" tabindex="-1">3.3、which <a class="header-anchor" href="#_3-3、which" aria-label="Permalink to &quot;3.3、which&quot;">​</a></h4><ul><li>可以看到别名 <code>which ls</code></li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># which ls</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># alias ls=&#39;ls --color=auto&#39;</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">#  /usr/bin/ls</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h4 id="_3-4、find" tabindex="-1"><strong>3.4、find</strong> <a class="header-anchor" href="#_3-4、find" aria-label="Permalink to &quot;**3.4、find**&quot;">​</a></h4><blockquote><p>文件搜索命令, <code>find [搜索范围] [搜索条件]</code></p></blockquote><p>3.4.1、按名称搜索</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># find / -name 11.txt</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>注意：避免大范围的搜索，会非常消耗系统资源</p><p>3.4.2、通配符</p><table><thead><tr><th>通配符</th><th>解释</th></tr></thead><tbody><tr><td><code>*</code></td><td>匹配任意内容</td></tr><tr><td><code>?</code></td><td>匹配任意一个字符</td></tr><tr><td><code>[]</code></td><td>匹配中括号内任意一个字符</td></tr></tbody></table><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># touch abc.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># find . -name &quot;ab[cdef].txt&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>3.4.3、-i 不区分大小写</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># find . -iname &quot;Ab[cdef].txt&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>3.4.4、按时间搜索</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># find . -mtime +5</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><table><thead><tr><th>参数</th><th>含义</th></tr></thead><tbody><tr><td><code>atime</code></td><td>文件访问时间</td></tr><tr><td><code>ctime</code></td><td>创建文件时间</td></tr><tr><td><code>mtime</code></td><td>修改文件时间</td></tr></tbody></table><table><thead><tr><th>参数</th><th>含义</th></tr></thead><tbody><tr><td><code>-5</code></td><td>5 天前之内修改的文件</td></tr><tr><td><code>5</code></td><td>5 天前当天修改的文件</td></tr><tr><td><code>+5</code></td><td>5 天前之前修改的文件</td></tr></tbody></table><p>3.4.5、按大小搜索</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># find . -size +0k</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><table><thead><tr><th>参数</th><th>含义</th></tr></thead><tbody><tr><td><code>-8k</code></td><td>小于 8K</td></tr><tr><td><code>8k</code></td><td>等于 8K</td></tr><tr><td><code>+8k</code></td><td>大于 8K</td></tr><tr><td><code>+8M</code></td><td>大于 8M</td></tr></tbody></table><p>3.4.6、综合搜索</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># find /tmp -size +10k -a -size -20k</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>查找 <code>/tmp</code> 目录下，大于 10KB 并且小于 20KB 的文件</li><li><code>-a and</code> 逻辑与，两个条件都满足</li><li><code>-o or</code> 逻辑或，两个条件满足一个就可以</li></ul><h4 id="_3-5、grep" tabindex="-1">3.5、grep <a class="header-anchor" href="#_3-5、grep" aria-label="Permalink to &quot;3.5、grep&quot;">​</a></h4><p>在文件当中匹配符合条件的字符串</p><ul><li><code>-i</code> 忽略大小写</li><li><code>-v</code> 排除指定字符串</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># a.txt</span></span>
<span class="line"><span style="color:#F78C6C;">123</span></span>
<span class="line"><span style="color:#FFCB6B;">abc</span></span>
<span class="line"><span style="color:#FFCB6B;">XYZ</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># grep b a.txt</span></span>
<span class="line"><span style="color:#FFCB6B;">abc</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># grep -v b a.txt</span></span>
<span class="line"><span style="color:#F78C6C;">123</span></span>
<span class="line"><span style="color:#FFCB6B;">XYZ</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># grep -i x a.txt</span></span>
<span class="line"><span style="color:#FFCB6B;">XYZ</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br></div></div><h3 id="四、文件压缩与解压" tabindex="-1">四、文件压缩与解压 <a class="header-anchor" href="#四、文件压缩与解压" aria-label="Permalink to &quot;四、文件压缩与解压&quot;">​</a></h3><h4 id="_4-1、zip" tabindex="-1">4.1、zip <a class="header-anchor" href="#_4-1、zip" aria-label="Permalink to &quot;4.1、zip&quot;">​</a></h4><p>一种压缩格式, 支持<strong>压缩文件或目录</strong></p><ul><li>安装</li></ul><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">yum install -y unzip zip</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><ul><li>命令</li></ul><table><thead><tr><th>功能</th><th>命令</th></tr></thead><tbody><tr><td>压缩文件</td><td><code>zip 压缩后文件名.zip 源文件</code></td></tr><tr><td>压缩文件</td><td><code>zip -r 压缩后文件名.zip 源文件</code></td></tr><tr><td>解压</td><td><code>unzip 压缩后目录名.zip</code></td></tr></tbody></table><ul><li>缺点</li></ul><p>压缩效率低</p><h4 id="_4-2、gzip" tabindex="-1">4.2、gzip <a class="header-anchor" href="#_4-2、gzip" aria-label="Permalink to &quot;4.2、gzip&quot;">​</a></h4><p><code>gzip</code> 为高压，可以把文件压缩得更小</p><ul><li>命令</li></ul><table><thead><tr><th>命令</th><th>示例</th><th>含义</th></tr></thead><tbody><tr><td><code>gzip 源文件</code></td><td>gzip book.txt</td><td>压缩为.gz 格式，删除源文件</td></tr><tr><td><code>gzip -c 源文件 &gt; 压缩文件</code></td><td>gzip -c book.txt &gt; book.txt.gz</td><td>压缩为.gz 格式，不会删除源文件</td></tr><tr><td><code>gzip -r 目录</code></td><td>gzip -r book</td><td>把目录下的<strong>每个层级的子文件</strong>都压缩，并删除原文件，当前目录无变化</td></tr><tr><td><code>gzip -d 压缩文件名</code></td><td>gzip -d 1.txt.gz</td><td>解压缩文件,不保留压缩包</td></tr><tr><td><code>gunzip 压缩文件</code></td><td>gunzip 2.txt.gz</td><td>解压缩文件,也不保留压缩包</td></tr></tbody></table><ul><li>缺点</li></ul><p>不支持压缩目录</p><h4 id="_4-3、tar" tabindex="-1">4.3、tar <a class="header-anchor" href="#_4-3、tar" aria-label="Permalink to &quot;4.3、tar&quot;">​</a></h4><p><code>tar</code> 打包命令,<strong>只打包并不压缩</strong></p><ul><li>命令 <ul><li><code>-c</code> 打包</li><li><code>-v</code> 显示过程</li><li><code>-f</code> 指定打包后的文件名</li><li><code>-x</code> 解开包</li></ul></li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 打包</span></span>
<span class="line"><span style="color:#FFCB6B;">tar</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-cvf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">打包文件名</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">源文件</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 解开包</span></span>
<span class="line"><span style="color:#FFCB6B;">tar</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-xvf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">打包文件名</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h4 id="_4-1、tar-gz" tabindex="-1">4.1、tar.gz <a class="header-anchor" href="#_4-1、tar-gz" aria-label="Permalink to &quot;4.1、tar.gz&quot;">​</a></h4><p>先打包为<code>.tar</code>格式，再压缩为<code>.gz</code>格式</p><table><thead><tr><th>命令</th><th>示例</th><th>含义</th></tr></thead><tbody><tr><td><code>tar -zcvf 压缩包名.tar.gz 源文件</code></td><td>tar -zcvf book.tar.gz book</td><td>先打包，再压缩</td></tr><tr><td><code>tar -zxvf 压缩包名.tar.gz</code></td><td>tar -zxvf book.tar.gz</td><td>解压 tar.gz 压缩包</td></tr></tbody></table><h3 id="五、文件查看命令" tabindex="-1">五、文件查看命令 <a class="header-anchor" href="#五、文件查看命令" aria-label="Permalink to &quot;五、文件查看命令&quot;">​</a></h3><h4 id="_5-1、cat" tabindex="-1">5.1、cat <a class="header-anchor" href="#_5-1、cat" aria-label="Permalink to &quot;5.1、cat&quot;">​</a></h4><p>用于连接文件并打印到标准输出设备上</p><ul><li>命令</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">cat</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">-AbeEnstTuv</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">--help</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">--version</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">fileName</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>参数 <ul><li><code>-n</code> 或 <code>--number</code>：从 1 开始对所有输出的行数进行编号</li></ul></li></ul><h4 id="_5-2、more" tabindex="-1">5.2、more <a class="header-anchor" href="#_5-2、more" aria-label="Permalink to &quot;5.2、more&quot;">​</a></h4><p>类似 cat ，不过会以一页一页的形式显示，更方便使用者逐页阅读</p><ul><li>命令</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">more</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">fileName</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>指令 <ul><li>按空白键（<code>space</code>）, 显示下一页</li><li>按 b 键（<code>back</code>）, 显示上一页</li></ul></li></ul><h4 id="_5-3、head" tabindex="-1">5.3、head <a class="header-anchor" href="#_5-3、head" aria-label="Permalink to &quot;5.3、head&quot;">​</a></h4><ul><li>命令</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">head</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">参数</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">文件名</span><span style="color:#89DDFF;">]</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>参数 <ul><li><code>-n&lt;行数&gt;</code>: 显示文件的头部前 n 行内容</li></ul></li></ul><h4 id="_5-4、tail" tabindex="-1">5.4、tail <a class="header-anchor" href="#_5-4、tail" aria-label="Permalink to &quot;5.4、tail&quot;">​</a></h4><p>用于查看文件的内容</p><ul><li>命令</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">tail</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">参数</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">文件名</span><span style="color:#89DDFF;">]</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>参数 <ul><li><code>-n&lt;行数&gt;</code>: 显示文件的尾部 n 行内容</li><li><code>-f</code>: 循环读取，<strong>跟踪文件变化</strong></li></ul></li></ul><h3 id="六、登录信息" tabindex="-1">六、登录信息 <a class="header-anchor" href="#六、登录信息" aria-label="Permalink to &quot;六、登录信息&quot;">​</a></h3><h4 id="_6-1、w" tabindex="-1">6.1、w <a class="header-anchor" href="#_6-1、w" aria-label="Permalink to &quot;6.1、w&quot;">​</a></h4><p>查看登录用户信息</p><table><thead><tr><th>输出 Title</th><th>含义</th></tr></thead><tbody><tr><td><code>USER</code></td><td>登录的用户名</td></tr><tr><td><code>TTY</code></td><td>登录的终端, <code>tty1</code> 本地终端; <code>pts/0</code> 远程终端</td></tr><tr><td><code>FROM</code></td><td>登录的 IP</td></tr><tr><td><code>LOGIN</code></td><td>登录时间</td></tr><tr><td><code>IDLE</code></td><td>用户闲置时间</td></tr><tr><td><code>JCPU</code></td><td>该终端所有进程占用的时间</td></tr><tr><td><code>PCPU</code></td><td>当前进程所占用的时间</td></tr><tr><td><code>WHAT</code></td><td>正在执行的命令</td></tr></tbody></table><h4 id="_6-2、who" tabindex="-1">6.2、who <a class="header-anchor" href="#_6-2、who" aria-label="Permalink to &quot;6.2、who&quot;">​</a></h4><p>查看登录用户信息</p><table><thead><tr><th>输出 Title</th><th>含义</th></tr></thead><tbody><tr><td><code>USER</code></td><td>登录的用户名</td></tr><tr><td><code>TTY</code></td><td>登录的终端, <code>tty1</code> 本地终端; <code>pts/0</code> 远程终端</td></tr><tr><td><code>LOGIN</code></td><td>登录时间（登录的 IP）</td></tr></tbody></table><h4 id="_6-3、last" tabindex="-1">6.3、last <a class="header-anchor" href="#_6-3、last" aria-label="Permalink to &quot;6.3、last&quot;">​</a></h4><p>查看当前登录和过去登录的用户信息, 默认读取 <code>/var/log/wtmp</code> 文件</p><h4 id="_6-4、lastlog" tabindex="-1">6.4、lastlog <a class="header-anchor" href="#_6-4、lastlog" aria-label="Permalink to &quot;6.4、lastlog&quot;">​</a></h4><p>查看所有用户的最后一次登录时间</p><h3 id="七、用户组和用户" tabindex="-1">七、用户组和用户 <a class="header-anchor" href="#七、用户组和用户" aria-label="Permalink to &quot;七、用户组和用户&quot;">​</a></h3><p>使用操作系统的人都是用户，用户组是具有相同系统权限的一组用户</p><h4 id="_7-1、配置文件" tabindex="-1">7.1、配置文件 <a class="header-anchor" href="#_7-1、配置文件" aria-label="Permalink to &quot;7.1、配置文件&quot;">​</a></h4><ul><li><code>/etc/group</code>: 存储当前系统中所有用户组信息</li><li><code>/etc/gshadow</code>: 存放当前系统中用户组的密码信息, 与 <code>etc/group</code> 中的记录一一对应</li><li><code>/etc/passwd</code>: 存储当前系统中所有用户的信息</li><li><code>/etc/shadow</code>: 存放当前系统中所有用户的密码信息</li></ul><h4 id="_7-2、命令操作" tabindex="-1">7.2、命令操作 <a class="header-anchor" href="#_7-2、命令操作" aria-label="Permalink to &quot;7.2、命令操作&quot;">​</a></h4><p><strong>用户组</strong></p><table><thead><tr><th>命令</th><th>示例</th><th>含义</th></tr></thead><tbody><tr><td><strong><code>groupadd xxx</code></strong></td><td><code>groupadd stu</code></td><td>添加用户组</td></tr><tr><td><strong><code>groupadd -g num xxx</code></strong></td><td><code>groupadd -g 666 teacher</code></td><td>添加用户组并指定编号</td></tr><tr><td><strong><code>groupmod -n newX oldX</code></strong></td><td><code>groupmod -n student stu</code></td><td>用户组重命名</td></tr><tr><td><strong><code>groupmod -g 999 xxx</code></strong></td><td><code>groupadd -g 999 student</code></td><td>修改用户组编号</td></tr><tr><td><strong><code>groupdel xxx</code></strong></td><td><code>groupdel student</code></td><td>删除用户组</td></tr></tbody></table><p><strong>用户</strong></p><table><thead><tr><th>命令</th><th>示例</th><th>含义</th></tr></thead><tbody><tr><td><strong><code>useradd -g &lt;group&gt; &lt;name&gt;</code></strong></td><td><code>useradd -g student Bob</code></td><td>创建用户并指定用户组</td></tr><tr><td><strong><code>useradd -d &lt;path&gt; &lt;name&gt;</code></strong></td><td><code>useradd -d /home/Jack Jack</code></td><td>创建用户并指定 Home 目录</td></tr><tr><td><strong><code>passwd &lt;name&gt;</code></strong></td><td><code>passwd Bob</code></td><td>root 用户可以设置用户的密码</td></tr><tr><td><strong><code>usermod -d &lt;path&gt; &lt;name&gt;</code></strong></td><td><code>usermod -d /home/Jack2 Jack</code></td><td>修改个人 Home 目录</td></tr><tr><td><strong><code>usermod -g &lt;group&gt; &lt;name&gt;</code></strong></td><td><code>usermod -g teacher Bob</code></td><td>修改用户组</td></tr><tr><td><strong><code>userdel &lt;name&gt;</code></strong></td><td><code>userdel Bob</code></td><td>删除用户</td></tr><tr><td><strong><code>userdel -r &lt;name&gt;</code></strong></td><td><code>userdel -r Bob</code></td><td>删除用户并删除对应的 Home 目录</td></tr><tr><td><strong><code>id &lt;name&gt;</code></strong></td><td><code>id Bob</code></td><td>显示用户的组信息</td></tr></tbody></table><h2 id="文件操作" tabindex="-1">文件操作 <a class="header-anchor" href="#文件操作" aria-label="Permalink to &quot;文件操作&quot;">​</a></h2><blockquote><p>当执行过 <code>chomd 777 xxx</code> 命令来设置权限，那数字 <code>777</code> 有何含义？</p></blockquote><h3 id="一、文件权限" tabindex="-1">一、文件权限 <a class="header-anchor" href="#一、文件权限" aria-label="Permalink to &quot;一、文件权限&quot;">​</a></h3><p>在任意目录下，执行 <code>ll</code> 命令，即可查看该目录下所有文件和目录的权限</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">root@VM-</span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;">-</span><span style="color:#F78C6C;">15</span><span style="color:#A6ACCD;">-centos </span><span style="color:#89DDFF;">~]</span><span style="color:#A6ACCD;"># ll</span></span>
<span class="line"><span style="color:#FFCB6B;">-rw-r--r--</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root</span><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">7月</span><span style="color:#A6ACCD;">   </span><span style="color:#F78C6C;">5</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">23</span><span style="color:#C3E88D;">:</span><span style="color:#F78C6C;">26</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span></span>
<span class="line"><span style="color:#FFCB6B;">drwxr-xr-x</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">4096</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">7月</span><span style="color:#A6ACCD;">   </span><span style="color:#F78C6C;">5</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">23</span><span style="color:#C3E88D;">:</span><span style="color:#F78C6C;">26</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">book</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>默认创建的文件和文件夹权限分别是 <code>-rw-r--r--</code> 和 <code>drwxr-xr-x</code>, 具体有哪些含义？</p><h4 id="_1-1、基本权限格式" tabindex="-1">1.1、基本权限格式 <a class="header-anchor" href="#_1-1、基本权限格式" aria-label="Permalink to &quot;1.1、基本权限格式&quot;">​</a></h4><p>以 <code>-rw-r--r--</code> 为例，权限一共有 10 个字符，从左到右，</p><p>第一个字符表示<strong>文件类型</strong>，具体区分是：</p><ul><li><code>-</code> 表示是文件</li><li><code>d</code> 表示是目录</li><li><code>l</code> 表示软链接文件</li></ul><p>后面每三个字符组成一组，共三组分别是：<strong>文件所有者</strong>、<strong>文件所有组</strong>、<strong>其他人</strong>。每一组中的三个字符分别表示 <strong><code>read</code>(可读)</strong>、<strong><code>write</code>(可写)</strong>、<strong><code>execute</code>(可执行)</strong> 这三个权限。</p><p>所以，权限<code>-rw-r--r--</code> 表示</p><ul><li>文件所有者 对该文件可读可写，不能执行 <code>rw-</code></li><li>文件所有组 和 其他用户 对该文件仅可读，不能写入，不能执行 <code>r--</code></li></ul><h4 id="_1-2、修改基本权限" tabindex="-1">1.2、修改基本权限 <a class="header-anchor" href="#_1-2、修改基本权限" aria-label="Permalink to &quot;1.2、修改基本权限&quot;">​</a></h4><ul><li><p>命令</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">选项</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">模式</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">文件名</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p>选项</p><ul><li><code>-R</code>: 递归</li></ul></li><li><p>模式</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">[ugoa][+-=][rwx]</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>三个<code>[]</code> 中字符代表什么意思呢？</p><p><strong>[ugoa]</strong>:</p><ul><li><code>u(user)</code>: 所有者</li><li><code>g(group)</code>: 所有组</li><li><code>o(other)</code>: 其他人</li><li><code>a(all)</code>: 所有人</li></ul><p><strong>[+-=]</strong>:</p><ul><li><code>+</code>: 添加权限</li><li><code>-</code>: 减少权限</li><li><code>=</code>: 直接赋值</li></ul><p><strong>[rwx]</strong>:</p><ul><li><code>r(read)</code>: 可读</li><li><code>w(write)</code>: 可写</li><li><code>x(execute)</code>: 可执行</li></ul><p>要修改的权限除了使用 👆 方法表示，还可以直接使用数字来表示，例如 <code>777</code>, 那么 <code>777</code> 啥意思？</p><p>以(<code>read</code>)为例，可读或不可读，内部使用二进制 <code>0</code>、<code>1</code> 来标识，那么最高权限 <code>rwx</code> 对应的二进制就是 <code>111</code>, 转换成十进制就是 <code>7</code>, 那么 <code>777</code> 表示所有者，所有组，以及其他人对文件都可以读、写、执行</p><p><code>rwx</code> 对应的二进制就是 <code>111</code>，每个权限对应的数字是</p><ul><li><code>r</code>: 4</li><li><code>w</code>: 2</li><li><code>x</code>: 1</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 所有者添加可写权限</span></span>
<span class="line"><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">u+w</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 其他人去除可写权限</span></span>
<span class="line"><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">o-w</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 最高权限</span></span>
<span class="line"><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">777</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div></li></ul><h4 id="_1-3、其它权限" tabindex="-1">1.3、其它权限 <a class="header-anchor" href="#_1-3、其它权限" aria-label="Permalink to &quot;1.3、其它权限&quot;">​</a></h4><h5 id="_1-3-1、chown" tabindex="-1">1.3.1、chown <a class="header-anchor" href="#_1-3-1、chown" aria-label="Permalink to &quot;1.3.1、chown&quot;">​</a></h5><p><code>chown(change owner)</code>, 修改文件所有者</p><ul><li>命令<div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">chown 用户名 文件名</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div></li></ul><h5 id="_1-3-2、chgrp" tabindex="-1">1.3.2、chgrp <a class="header-anchor" href="#_1-3-2、chgrp" aria-label="Permalink to &quot;1.3.2、chgrp&quot;">​</a></h5><p><code>chgrp(change group)</code>, 修改文件所有组</p><ul><li>命令<div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">chown 组名 文件名</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div></li></ul><h3 id="二、默认权限" tabindex="-1">二、默认权限 <a class="header-anchor" href="#二、默认权限" aria-label="Permalink to &quot;二、默认权限&quot;">​</a></h3><p>文件和目录在创建时，都有默认的权限，具体是什么，以及如何修改默认权限呢？</p><h4 id="_2-1、umask" tabindex="-1">2.1、umask <a class="header-anchor" href="#_2-1、umask" aria-label="Permalink to &quot;2.1、umask&quot;">​</a></h4><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">umask</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 0022</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><code>umask</code> 命令指定在建立文件时预设的<strong>权限掩码</strong>, 默认值为 <code>0022</code></p><ul><li>第一位 0 表示文件的特殊权限</li><li>后面 <code>022</code> 才是我们需要的</li></ul><h4 id="_2-2、文件默认权限" tabindex="-1">2.2、文件默认权限 <a class="header-anchor" href="#_2-2、文件默认权限" aria-label="Permalink to &quot;2.2、文件默认权限&quot;">​</a></h4><p>默认创建的文件是不可执行文件，必须手工赋予执行权限，所以文件默认权限最大值为 <code>666</code>, 而默认创建的文件权限为 666 减去 <code>umask</code> 值</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">666</span><span style="color:#A6ACCD;"> - </span><span style="color:#F78C6C;">022</span><span style="color:#A6ACCD;"> = </span><span style="color:#F78C6C;">644</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 655 =&gt; \`rw-r--r--\`</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>也就是 <code>rw-r--r--</code></p><h4 id="_2-3、目录默认权限" tabindex="-1">2.3、目录默认权限 <a class="header-anchor" href="#_2-3、目录默认权限" aria-label="Permalink to &quot;2.3、目录默认权限&quot;">​</a></h4><p>目录默认权限最大值为 777, 所以默认创建的目录权限为 777 减去 <code>umask</code> 值</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">777</span><span style="color:#A6ACCD;"> - </span><span style="color:#F78C6C;">022</span><span style="color:#A6ACCD;"> = </span><span style="color:#F78C6C;">755</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 755 =&gt; \`rwxr-xr-x\`</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h4 id="_2-4、修改-umask-值" tabindex="-1">2.4、修改 umask 值 <a class="header-anchor" href="#_2-4、修改-umask-值" aria-label="Permalink to &quot;2.4、修改 umask 值&quot;">​</a></h4><ul><li><p>临时修改</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">umask</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0002</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p>永久修改</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">vi</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/profile</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li></ul><h3 id="三、总结" tabindex="-1">三、总结 <a class="header-anchor" href="#三、总结" aria-label="Permalink to &quot;三、总结&quot;">​</a></h3><ul><li><p>共有三组角色可以操作一份文件，分别是<strong>文件所有者</strong>、<strong>文件所有组</strong>、<strong>其他人</strong></p></li><li><p>对文件可执行 <strong><code>read</code>(读)</strong>、<strong><code>write</code>(写)</strong>、<strong><code>execute</code>(执行)</strong> 这三种操作，分别对应三个权限<code>rwx</code></p></li><li><p>最高权限<code>777</code>, 最低权限<code>000</code>,可通过 <code>chmod</code> 命令修改权限</p></li><li><p><code>umask</code> 值决定了创建文件、目录时的默认权限</p></li></ul>`,164),o=[t];function d(r,c,p,i,h,u){return s(),e("div",null,o)}const y=a(n,[["render",d]]);export{m as __pageData,y as default};
